import Swal from 'sweetalert2';

export default (isCorrect) => {
    if (isCorrect == -1)
        Swal.fire({
            position: 'top-end',
            type: 'error',
            title: 'Oops',
            text: 'You are not logged in',
            showConfirmButton: false,
            timer: 1500
        })
    if (isCorrect === 1)
        Swal.fire({
            position: 'top-end',
            icon:'success',
            type: 'success',
            title: 'You gave the correct answer',
            showConfirmButton: false,
            timer: 1500
        })
    if (isCorrect === 0)
        Swal.fire({
            position: 'top-end',
            icon:'error',
            title: 'You gave the wrong answer',
            text: 'Try again.',
            type: 'error',
            showConfirmButton: false,
        })
}
