    {
        console.log("yo");
        FB.logout()
        Router.push('/');
    }

  render() {
    return (
        <div>
            <div style={{ margin: "auto", textAlign: "center", paddingTop: "2em" }}>
            { this.state.user ? 
            <div> 
                <UserScreen user={this.state.user}/> 
                <Button
                  variant="contained"
                  color="secondary"
                   onClick={this.logout}

                  >
                    Log Out
                  </Button>

                  <Button variant="contained" color="secondary" href='/game'>
                    Play
                  </Button>
            </div>
            :
            <LoginButton facebookResponse={this.facebookResponse}/>
            }
        </div>



        </div>


    )
  }
}

export default App
