import React from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import Link from "next/link";
import MenuItem from "@material-ui/core/MenuItem";
import MenuIcon from "@material-ui/icons/Menu";
import Menu from "@material-ui/core/Menu";
import { useState, useEffect } from "react";
import Fade from "@material-ui/core/Fade";
import Rules from "../components/Rules";


const useStyles = makeStyles((theme)={
    root: {
        "& > *": {
          margin: theme.spacing(1),
        },
      },
  list: {
    width: 250,
  },
  fullList: {
    width: 'auto',
  },
});

export default function SwipeableTemporaryDrawer() {
  const classes = useStyles();
  const [state, setState] = React.useState({

    left: false

  });
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);


  const [Log, setLog] = useState(false);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  useEffect(() => {
    console.log("2");
    setLog(localStorage.getItem("email"));
    console.log(Log);
  }, []);

  const logout = () => {
    //FB.logout();
    clearTimeout(localStorage.getItem("interval_id"));
    localStorage.clear();
    Router.push("/");
  };


  const toggleDrawer = (anchor, open) => (event) => {
    if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };



  return (
    <div>
      {['left'].map((anchor) => (
        <React.Fragment key={anchor}>

          <Button
        aria-controls="fade-menu"
        style={{ color: "white" }}
        aria-haspopup="true"
        onClick={toggleDrawer(anchor, true)}
      >
        <MenuIcon />
      </Button>
          <SwipeableDrawer
            anchor={anchor}
            open={state[anchor]}
            onClose={toggleDrawer(anchor, false)}
            onOpen={toggleDrawer(anchor, true)}
          >
            <div
            className={clsx(classes.list, {
                [classes.fullList]: anchor === 'top' || anchor === 'bottom',
            })}
            role="presentation"
            onClick={toggleDrawer(anchor, false)}
            onKeyDown={toggleDrawer(anchor, false)}
            >
                  <Menu
        id="fade-menu"
        anchorEl={anchorEl}
        keepMounted
        open={open}
        onClose={handleClose}
        TransitionComponent={Fade}
        marginThreshold="0px"
        anchorOrigin={{ vertical: "top", horizontal: "left" }}
        anchorPosition={{ left: "0", top: "0" }}
        transformOrigin={{ top: "0px", left: "0px" }}
        style={{ fontFamily: "'Barlow', sans-serif" }}
        PaperProps={{
          style: {
            top: 0,
            left: 0,
            backgroundColor: "#cc0044",
            borderRadius: "6px",
            color: "white",
            marginTop: "0",
            paddingTop: ["0px"],
            width: "400px",
            maxHeight: "100%",
          },
        }}
      >
        <Link href="/">
          <MenuItem onClick={handleClose}>Home</MenuItem>
        </Link>
        <MenuItem onClick={handleClose}>
          <Rules>Rules</Rules>
        </MenuItem>

        <Link href="/leaderboard">
          <MenuItem onClick={handleClose}>Leaderboard</MenuItem>
        </Link>
        {Log ? (
          <div className="userDetails-wrapper">
            <div className="details-wrapper">
              <GoogleLogout
                render={(renderProps) => (
                  <div className={classes.root}>
                    <Link href="/">
                      <MenuItem onClick={logout}>Logout</MenuItem>
                    </Link>
                  </div>
                )}
                onLogoutSuccess={logout}
              />
            </div>
            <div className="bar" />
            <div className="stand" />
          </div>
        ) : (
          <Link href="/game">
            <MenuItem onClick={handleClick}>Game</MenuItem>
          </Link>
        )}
      </Menu>

            </div>
          </SwipeableDrawer>
        </React.Fragment>
      ))}
    </div>
  );
}
