import React, { Component } from "react";
import { withStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";


const useStyles = (theme) => ({
    root: {
        "& > *": {
            margin: theme.spacing(0),
            width: "10px",
        },
        anstype: {
            width: "600px"
        },
        ansdiv: {
            width: "600px"
        }

    },
    ans: {
        width: "90%",
        maxWidth: "400px",
        height: "50px",
        outline: "none",
        border: "none",
        borderWidth: "20px",
        backgroundColor: "rgba(3, 3, 65, 0.8)"
    }
});

class Answer extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        const { classes } = this.props;
        return ( <
            div >
            <
            form className = { classes.ansdiv }
            noValidate autoComplete = "off"
            onSubmit = { this.props.submit2 } >
            <
            TextField id = "filled-basic"
            label = "Type your answer here."
            variant = "filled"
            onChange = { this.props.change }

            value = { this.props.answer }
            className = { classes.anstype }
            style = {
                { maxWidth: "600px", width: "80%", } }
            /> <
            input className = { classes.ans }
            /> <
            br / >
            <
            br / >

            <
            /form> <
            /div>
        );
    }
}
export default withStyles(useStyles)(Answer);
