import React, { useState, useEffect } from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Typography from "@material-ui/core/Typography";
import axios from "axios";
import data from '../env.json';
import Avatar from "@material-ui/core/Avatar";
import Loader from "./Loader";

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: "#000044",
    color: "white",
    border: "none",
    fontFamily: "Russo One",
    fontSize: 16
  },
  body: {
    fontFamily: "Russo One",
    fontSize: 14,
    color: "#fff",
    border: "none",
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    "&:nth-of-type(1)": {
      backgroundColor: "rgba(0,200,0,0.4) !important",
      color: "#000 !important"
    },
    "&:nth-of-type(2)": {
      backgroundColor: "rgba(0,200,0,0.4) !important",
      color: "#000 !important"
    },
    "&:nth-of-type(3)": {
      backgroundColor: "rgba(0,200,0,0.4) !important",
      color: "#000 !important"
    },
    "&:nth-of-type(odd)": {
      backgroundColor: "rgba(55,55,100,0.3)",
      color: "#000 !important"
    },
    "&:nth-of-type(even)": {
      backgroundColor: "rgba(0,0,0,0.7)",
      color: "white !important",
    },
  },
}))(TableRow);

const useStyles = makeStyles({
  table: {
    margin:"15px auto",
    maxWidth: "900px",
    width:"90%",
    overflowX: "hidden",
  },
});

export default function Leadertable() {
  const [RankList, setRankList] = useState(false);
  const [loaded, setloaded] = useState([]);
  const classes = useStyles();

  useEffect(() => {
    axios
      .get(data.api + "/api/leaderboard")
      .then((response) => {
        setRankList(response.data);

      })
      .then(() => {
        setloaded(true);
      });
  }, []);

  return (
    <div>
      { (loaded === true) ?
        <div className="rtable">
          <Table className={classes.table} aria-label="customized table">
            <TableHead>
              <TableRow>
                <StyledTableCell>Rank</StyledTableCell>
                <StyledTableCell></StyledTableCell>
                <StyledTableCell align="left">Player</StyledTableCell>
                <StyledTableCell align="left">Points</StyledTableCell>
              </TableRow>
            </TableHead>
            {RankList.length ? (
              <TableBody style={{ fontFamily: "'Barlow', sans-serif" }}>
                {RankList.map((row, index) => (
                  <StyledTableRow
                    key={index}
                    style={{ fontFamily: "'Barlow', sans-serif" }}
                  >
                    <StyledTableCell component="th" scope="row">
                      {row.rank}.
                  </StyledTableCell>
                    <StyledTableCell align="left">
                      <Avatar
                        alt={name}
                        src={row.imgurl}
                      /></StyledTableCell>
                    <StyledTableCell>{row.name}</StyledTableCell>
                    <StyledTableCell align="left">{row.score}</StyledTableCell>
                  </StyledTableRow>
                ))}
              </TableBody>
            ) : (
                <Typography
                  variant="h6"
                  className={classes.title}
                  style={{ fontFamily: "'Russo One', sans-serif", fontSize: 18, color: 'white' }}
                >
                  &nbsp;&nbsp;&nbsp;Loading...
                </Typography>
              )}
          </Table>
        </div>
        : <Loader />}
    </div>
  );
}
