    {
        console.log("yo");
        FB.logout()

    }

  render() {
    return (
        <div>
            <div style={{ margin: "auto", textAlign: "center", paddingTop: "2em" }}>
            { this.state.user ? 
            <div> 
                <UserScreen user={this.state.user}/> 
                <Button
                  variant="contained"
                  color="secondary"
                   onClick={this.logout}
                    href='/'
