                    Log Out
                  </Button>

                  <Button variant="contained" color="secondary" href='/game'>
                    Play
                  </Button>
            </div>
            :
            <LoginButton facebookResponse={this.facebookResponse}/>
            }
        </div>



        </div>


    )
  }
}

export default App
