import Swal from 'sweetalert2';

export default (isCorrect) => {
    if (isCorrect == -1)
        Swal.fire({
            padding: "60px",
            position: 'center',
            type: 'success',
            title: 'Yay',
            text: 'Successfully registered !',
            showConfirmButton: false

        })
    if (isCorrect === 1)
        Swal.fire({
            padding: "60px",
            position: 'center',
            icon: 'success',
            type: 'success',
            title: 'You gave the correct answer',
            showConfirmButton: false

        })
    if (isCorrect === 0)
        Swal.fire({
            padding: "60px",
            position: 'center',
            icon: 'error',
            title: 'Oops! Wrong answer.',
            text: 'Try again',
            showConfirmButton: false

        })
        if (isCorrect === 9)
        Swal.fire({
            padding: "60px",
            position: 'center',
            icon: 'error',
            title: 'Time.',
            text: 'Try again',
            showConfirmButton: false

        })


}
