import axios from "axios";
import React from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import Question from "../components/Question";
import Hint from "../components/Hint";

import { withAlert } from "react-alert";

import Particles from "react-particles-js";
import Answer from "../components/Answer";
import Router from "next/router";

import AudioHint from "../components/AudioHint";
const useAlert=()=>
class game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      answer: "",
      questions: "",
      qsNo: 1,
      audio: "",
      image: "",
      isLoggedIn: false,
      hint: "",
    };

    this.submit = this.submit.bind(this);
    this.submit2 = this.submit2.bind(this);
    this.change = this.change.bind(this);
    this.checkAns = this.checkAns.bind(this);
    this.getQuestions = this.getQuestions.bind(this);
  }


  componentDidMount() {
    //revert back to index if not logged in
    if(!localStorage.getItem('email'))
    {
      alert("You are not logged in. ;_;")
      Router.push('/');
    }

    //at the beginning sets the qs
    //after getting appropiate data from backend about that email in local storage
    console.log(this.state.qsNo);
    this.getQuestions();
  }

  getQuestions() {
   //get questions from api and updates state
    axios
      .get("https://screencast20.azurewebsites.net/api/question", {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((response) => {
        console.log(response);
        if (response.data.quiz_finished) Router.push("/finale");
        this.setState((prevState) => {
          return {
            ...prevState,
            questions: response.data.question,
            hint: response.data.hint,
            qsNo: response.data.question_no,
            audio: response.data.audio,
            image: response.data.image,
          };
        });
      });
    console.log(localStorage.getItem("token"));
  }

  submit = (event) => {
    //send final answer for checking
    if (event.key === "Enter") {
      console.log(this.state.answer);
      console.log("JOJOJOJOJOJ");
      this.checkAns(this.state.answer);
    }
  };
  submit2 = () => {
    //send final answer for checking

    console.log(this.state.answer);

    this.checkAns(this.state.answer);
  };

  change = (event) => {
    //keep updating answer
    let e = event.target.value;
    this.setState((prevState) => {
      return { ...prevState, answer: e };
    });
  };

  checkAns(
    ans //check answer from api and send for correct alert
  ) {
    console.log(ans);
    console.log(this.state.qsNo);
    axios
      .post(
        "https://screencast20.azurewebsites.net/api/checkanswer",
        { answer: ans },
        {
          headers: {
            Authorization: "Bearer " + localStorage.getItem("token"),
          },
        }
      )
      .then((response) => {
        let r = response.data.result;
        console.log(response);

        if (r && !response.data.quiz_finished) {
          this.setState((prevState) => {
            return { ...prevState, qsNo: prevState.qsNo + 1 };
          });
          this.props.alert.show("Correct"); //where does the effing control go after this?
          console.log("SANTA");

          this.getQuestions();
        } else if (r && response.data.quiz_finished) {
          alert("Completed!!!");
          Router.push("/finale");
        } else {
          this.props.alert.show("This is an alert with title!")
            //fjhdfb
        }
      });
  }

  render() {

    return (
      <div
        style={{ marginRight: "auto", marginLeft: "auto", textAlign: "center" }}
        questions
      >
        <Particles
          params={{
            particles: {
              number: {
                value: 40,
                density: {
                  enable: false,
                  value_area: 500,
                },
              },
              color: {
                value: ["#cc0000"],
              },
              line_linked: {
                color: "#aa0044",
                opacity: 1,
              },
            },
          }}
          style={{
            width: "100%",
            height: "100%",
            top: 0,
            left: 0,
            position: "fixed",
            index: "-10",
          }}
        />

        <Navbar />

        <Question
          qs={this.state.questions}
          qsNo={this.state.qsNo}
          image={this.state.image}
          audio={this.state.audio}
        />

        <div>
          <Answer
            change={this.change}
            submit={this.submit2}
            submit2={this.submit}
          />

          <Hint hint={this.state.hint} />
          <style jsx>{`
            div {
              text-align: center;
              margin: 5px;
            }
          `}</style>
        </div>
        <div>
          <Footer />
        </div>
      </div>
    );
  }
}
export default withAlert()(game);
