import React, { useEffect, useState } from "react";
import Head from "next/head";
import axios from "axios";
import GoogleLog from "../components/GoogleLog";
import FbLog2 from "../components/FbLog2";
import Timer2 from "../components/Timer2"
import Router from "next/router";
import data from '../env.json';
import Layout from "../components/Layout";

function index() {

    const [end, setEnd] = useState(Date.now());
    const [start, setStart] = useState(Date.now() + 2000);
    const [day, setDay] = useState(0);

    useEffect(() => {
        console.log('index mounted')

        axios
            .get(data.api + "/api/status")
            .then((response) => {
                console.log(response);
                if (response.data.status == 404)
                    Router.push('/error')
                var temp = new Date(response.data.start_time);
                setStart(temp.getTime() + (temp.getTimezoneOffset() * 60000));
                let temp2 = new Date(response.data.end_time);
                setEnd(temp2.getTime() + (temp2.getTimezoneOffset() * 60000));
                setDay(response.data.current_day)

                localStorage.setItem("start", start);
                localStorage.setItem("end", end);
                localStorage.setItem("day", day);
                //if(localStorage.getItem('email'))
                console.log(localStorage.getItem('end'))
                console.log(localStorage.getItem('day') == 3 && (localStorage.getItem('end') < Date.now()))
                console.log(localStorage.getItem('email') && (localStorage.getItem('start') < Date.now()) && end > Date.now())
                if (localStorage.getItem('day') == 3 && (localStorage.getItem('end') < Date.now()))
                    Router.push('/game_finale')


                if (localStorage.getItem('email') && (localStorage.getItem('start') < Date.now()) && localStorage.getItem('end') > Date.now()) {
                    Router.push('/game')
                        //console.log(Date.now()+"    "+localStorage.getItem('start'))
                        //Router.push('/game')
                }



                //if(localStorage.getItem('end') < Date.now()) Router.push('/finale2');
            });
    })

    return (



        <
        Layout >
        <
        div style = {
            { textAlign: "center", minHeight: "100vh" } } >




        <
        Timer2 start = { start }
        end = { end }
        />

        <
        div >
        <
        div className = "sign"
        style = {
            {
                marginTop: "40px",
                marginBottom: "20px",
                fontFamily: "'Russo One', sans-serif",
            }
        } >
        <
        span className = "fast-flicker" > screen < /span>cast{" "} <
        span className = "flicker" > 2020 < /span> <
        /div>

        <
        div className = "login-head"
        style = {
            { marginBottom: "60px" } } >
        <
        h1 > Ready to play ? < /h1> <
        /div> <
        /div>

        <
        div className = 'rowC'
        style = {
            { margin: "0 auto", marginBottom: '100px', textAlign: "center", width: "100%", justifyContent: "center" } } >
        <
        GoogleLog / >
        <
        FbLog2 / >
        <
        /div>

        <
        div style = {
            { paddingTop: '70px' } } >

        <
        /div>

        <
        /div>

        <
        /Layout>



    );
}

export default index;
