import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Avatar from "@material-ui/core/Avatar";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";

import Test from "./Test"


import styles from "./Navbar.module.css";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 0,
    backgroundColor: "#000044",
    width: "100%",
    padding: 0,
  },
  menuButton: {
    marginRight: theme.spacing(0),
  },
  title: {
    flexGrow: 0,
  },
  ava: {
    flexGrow: 0,
    marginLeft: "auto",
  },
}));

export default function Navbar(props) {

  const classes = useStyles();


  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  //to display avatar in logged in state
  const [isSignedIn, setIsSigned] = useState(false);
  const [name, setName] = useState("");
  const [image, setImage] = useState("");

  useEffect(() => {
    console.log("Nav mounted")
    if (localStorage.getItem("email")) {
      setIsSigned(true);
      setName(localStorage.getItem("name"));
      setImage(localStorage.getItem("image"));
    }
  });
  console.log("Just b4 return of nav")
  return (
    <div className={classes.root}>
      <AppBar position="static" className={styles.navv} style={{ padding: "0px" }}>
        <Toolbar className={styles.navcontent}>
          <IconButton
            edge="start"
            className={classes.menuButton}
            color="white"
            aria-label="menu"
            style={{ padding: "0" }}
          >
            <Test />
          </IconButton>
          {/* <img src={logo} alt="." style={{ width: "45px", borderRadius: "50px", boxShadow: "1px 1px 2px #000" }} /> */}
          <Typography
            variant="h6"
            className={classes.title}
            style={{ fontFamily: " sans-serif" }}
          >

            &nbsp;&nbsp;&nbsp;SCREENCAST 2020
          </Typography>

          <div className={classes.ava}>
            {isSignedIn ? (
              <div>

                <Avatar
                  position="right"
                  alt={name}
                  src={image}
                  className={styles.ava}
                  style={{
                    textAlign: "right",
                  }}
                />

              </div>
            ) : (
                <div></div>
              )}
          </div>

        </Toolbar>

      </AppBar>
      <div>

      </div>


    </div>
  );
}
