import React from 'react';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Fade from '@material-ui/core/Fade';
import MenuIcon from '@material-ui/icons/Menu';
import Link from 'next/link'


export default function FadeMenu() {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };



  return (
    <div>
      <Button aria-controls="fade-menu" aria-haspopup="true" onClick={handleClick}>
        <MenuIcon />
      </Button>
      <Menu

        id="fade-menu"
        anchorEl={anchorEl}
        keepMounted
        open={open}
        onClose={handleClose}
        TransitionComponent={Fade}
        marginThreshold='0px'
        anchorOrigin={{ vertical: "top", horizontal: "left" }}
        anchorPosition={{ left: "0", top: "0" }}
        transformOrigin={{ top: "0px", left: "0px" }}

        PaperProps={{
          style: {
            top: 0,
            left: 0,
            backgroundColor: '#cc0044',
            borderRadius: '3px',
            color: 'white',
            marginTop: '0',
            paddingTop: ['0px'],
            width: '400px',
            maxHeight: '100%'
          },
        }}
      >


        <Link href="/"><MenuItem onClick={handleClose}>Home</MenuItem></Link>
        <Link href="/game"><MenuItem onClick={handleClose}>Game</MenuItem></Link>
        <Link href="/leaderboard"><MenuItem onClick={handleClose}>Leaderboard</MenuItem></Link>


      </Menu>
      <style jsx>{`
      margin:0;
      padding:0;

      `}</style>
    </div >
  );
}
