const fs = require("fs")
const path = require("path")
const HTMLWebpackPlugin = require("html-webpack-plugin")


module.exports = {
    entry: fs.readdirSync(path.resolve(__dirname, "example")).filter(d => !d.startsWith(".")).reduce((obj, dir) => {
        return { ...obj, [+/\d+/.exec(dir)]: path.resolve(__dirname, "example", dir, "index.tsx") }
    }, {}),
    output: {
        publicPath: "/examples/",
        filename: "[name].js",
    },
    resolve: {
        extensions: [".js", ".ts", ".tsx"]
    },
    devtool: "inline-source-map",
    module: {
        rules: [
            {
                test: /\.tsx?$/,
                loader: "ts-loader",
                exclude: /node_modules/
            }
        ]
    },
    plugins: [
        new HTMLWebpackPlugin()
    ]
}